func makeLogo() string {
	buf := new(bytes.Buffer)

	logoTemplate, _ := template.New("logo").Parse(doomLogoTemplate)
	logoTemplate.Execute(buf, tview.Styles)

	//return buf.String()

	return "foo"
}
