func makeLogo() string {
	buf := new(bytes.Buffer)

	logoTemplate, _ := template.New("logo").Parse(doomLogoTemplateTwadTheme)
	if config.UseTerminalColors {
		logoTemplate, _ = template.New("logo").Parse(doomLogoTemplateTerminalTheme)
	}
	logoTemplate.Execute(buf, tview.Styles)

	return buf.String()
}
